import { playersBestObjArray, comparisonArray } from "./FormattedData";
// import Chart from "./components/Chart";
import LineChart from "./components/LineChart";
import Carousel from "./components/Carousel";
import { useState } from "react";
import Dropdowns from "./components/Dropdowns";

const ComparePopup = ({ onClose }) => {

    // const foundPlayer = playersBestObjArray.find(obj => obj.name === name);
    const [playersSelected, setPlayersSelected] = useState(false);
    const [optionOne, setOptionOne] = useState('');
    const [optionTwo, setOptionTwo] = useState('');

    const handleCompareClick = () => {

        const playerOne = playersBestObjArray.find(obj => obj.name === optionOne);
        const playerTwo = playersBestObjArray.find(obj => obj.name === optionTwo);
        

        setPlayersSelected(true);

    };


    return (
        <div className="overlay">
            <div className="popup-container">
                
                {!playersSelected && <div>

                    <div>Choose two players to compare:</div>
                    <Dropdowns setOption={setOptionOne} />
                    <Dropdowns setOption={setOptionTwo} />
                    <div>
                    <button className="compare-button" onClick={handleCompareClick}>
                        Compare
                    </button>
                    <button className="popup-button" onClick={onClose}>
                        Close
                    </button>
                    </div>

                </div>}

                {playersSelected && 
                <div>
                    <div className="top-section">
                        <div className="text">
                            aa
                            {/* <p>{name}</p> */}
                            {/* <p>Total Years Played: {foundPlayer.yearsPlayed}</p> */}
                        </div>
                    </div>
                    <div>——————————————</div>
                    <Carousel playerOne={[1,2,3,1]} playerTwo={[2,3,1,3]}/>
                    <div className="bottom-section">
                        bb
                        {/* <p>Best Completion %: {foundPlayer.completionPerc}</p>
                        <p>Best Yards: {foundPlayer.yards}</p>
                        <p>Best Interceptions: {foundPlayer.int}</p>
                        <p>Best Rate: {foundPlayer.rate}</p> */}
                    </div>

                    <button className="popup-button" onClick={onClose}>
                        Close
                    </button>
                </div>
                }
            </div>
        </div>
    );
};

export default ComparePopup;
