import React, { useState } from 'react';
import LineChart from './LineChart';

const TabContent = ({ playerOneData, playerTwoData }) => (
  <div>
    <div style={{ float: 'left', width: '50%' }}>
      <LineChart data={playerOneData} />
    </div>
    <div style={{ float: 'right', width: '50%' }}>
      {/* <LineChart data={playerTwoData} /> */}
      right content
    </div>
  </div>
);

const Carousel = ({ playerOne, playerTwo }) => {
  const [activeTab, setActiveTab] = useState(1);

  const handleTabChange = (direction) => {
    setActiveTab((prevTab) => {
      if (direction === 'left') {
        return prevTab > 1 ? prevTab - 1 : 4;
      } else {
        return prevTab < 4 ? prevTab + 1 : 1;
      }
    });
  };

  return (
    <div className="carousel-container">
      <div className="tab">
        <button onClick={() => handleTabChange('left')}>Left</button>
        <div className={`tab-content ${activeTab === 1 ? 'active' : ''}`}>
          <TabContent playerOneData={ playerOne.completionPerc } playerTwoData={ playerTwo.completionPerc }/>
        </div>
        <div className={`tab-content ${activeTab === 2 ? 'active' : ''}`}>
          <TabContent playerOneData={ playerOne.yards } playerTwoData={ playerTwo.yards }/>
        </div>
        <div className={`tab-content ${activeTab === 3 ? 'active' : ''}`}>
          <TabContent playerOneData={ playerOne.int } playerTwoData={ playerTwo.int }/>
        </div>
        <div className={`tab-content ${activeTab === 4 ? 'active' : ''}`}>
          <TabContent playerOneData={ playerOne.rate } playerTwoData={ playerTwo.rate }/>
        </div>
        <button onClick={() => handleTabChange('right')}>Right</button>
      </div>
    </div>
  );
};

export default Carousel;
