import { comparisonArray } from "./FormattedData";
import Carousel from "./components/Carousel";
import { useState } from "react";
import Dropdowns from "./components/Dropdowns";

const ComparePopup = ({ onClose }) => {

    const [playersSelected, setPlayersSelected] = useState(false);
    const [optionOne, setOptionOne] = useState('');
    const [optionTwo, setOptionTwo] = useState('');

    const handleCompareClick = () => {
        console.log('Before Update - Player One:', playerOne);
        console.log('Before Update - Player Two:', playerTwo);
      
        let player = comparisonArray.find(obj => obj.name === optionOne);
        updatePlayerOne(prevPlayerOne => ({ ...prevPlayerOne, ...player }));
      
        player = comparisonArray.find(obj => obj.name === optionTwo);
        updatePlayerTwo(prevPlayerTwo => ({ ...prevPlayerTwo, ...player }));
      
        console.log('After Update - Player One:', playerOne);
        console.log('After Update - Player Two:', playerTwo);
      
        setPlayersSelected(true);
      };
      

    const updatePlayerOne = (value) => {
        setPlayerOne(value);
    };

    const updatePlayerTwo = (value) => {
        setPlayerTwo(value);
    };


    return (
        <div className="overlay">
            <div className="popup-container">
                
                {!playersSelected && <div>

                    <div>Choose two players to compare:</div>
                    <Dropdowns setOption={setOptionOne} />
                    <Dropdowns setOption={setOptionTwo} />
                    <div>
                    <button className="compare-button" onClick={handleCompareClick}>
                        Compare
                    </button>
                    <button className="popup-button" onClick={onClose}>
                        Close
                    </button>
                    </div>

                </div>}

                {playersSelected && 
                <div>
                    <div className="top-section">
                        <div className="text">
                            stat 
                            {/* put stat name here if possible */}
                        </div>
                    </div>
                    <div>——————————————</div>
                    <Carousel playerOne={comparisonArray.find(obj => obj.name === optionOne)} playerTwo={comparisonArray.find(obj => obj.name === optionTwo)}/>

                    <button className="popup-button" onClick={onClose}>
                        Close
                    </button>
                </div>
                }
            </div>
        </div>
    );
};

export default ComparePopup;
